class Solution {
public:
    vector<vector<int>> generateMatrix(int n) {
        vector<vector<int>> res(n, vector<int>(n, 0)); // vector
        int startx = 0, starty = 0; // 
        int loop = n / 2; // n3loop = 1 
        int mid = n / 2; // n3 (11)n5(2, 2)
        int count = 1; // 
        int offset = 1; // 
        int i,j;
        while (loop --) {
            i = startx;
            j = starty;

            // for
            // ()
            for (j; j < n - offset; j++) {
                res[i][j] = count++;
            }
            // ()
            for (i; i < n - offset; i++) {
                res[i][j] = count++;
            }
            // ()
            for (; j > starty; j--) {
                res[i][j] = count++;
            }
            // ()
            for (; i > startx; i--) {
                res[i][j] = count++;
            }

            // 1 (0, 0)(1, 1)
            startx++;
            starty++;

            // offset 
            offset += 1;
        }

        // n
        if (n % 2) {
            res[mid][mid] = count;
        }
        return res;
    }
};
